import styled from 'styled-components'


export const Main = styled.div`
define([
    'require',
    'dependency'
], function(require, factory) {
    'use strict';

});`

export const Wrapper = styled.div`
width:300px;
height:300px;
background-color:${({light})=>light?'red':`green`};
`
